import React, { useState } from "react";
import ReactDatePicker from "react-datepicker";
import { useGlobalContext } from "./context/globalcontext";

import axios from "axios";




const BASE_URL = "http://localhost:3000/r/";


function Form(){
const {addIncome} = useGlobalContext() ;
   
    const [state , setstate] = useState({
        title  :'' ,
        date :  '' ,
        amount : '' , 
        category : '' ,
        note : '' ,
    })
    

  const { title, amount, date, category,description } = state;


    const handleInput = name => e => {
        setstate({...state, [name]: e.target.value})
      
    }
    const handelsubmit = e=>{
        e.preventDefault() ;
        addIncome(state) ;
        setstate({
            title: '',
            amount: '',
            date: '',
            category: '',
            description: '',
        })
    }
     
  


    return(
        <div>
          
            <input type="text"   onChange={handleInput('title')} ></input>
            <input type="date" onChange={(date)=>{
                setstate({...state , date : date}) 
            }}></input>
            <input type="number" onChange={handleInput('amount')}></input>
            <select name="category" onChange={handleInput('category')}></select>
        </div>
    )
}

export default Form
