import React, { useContext, useEffect, useState } from "react"
import axios from 'axios'


const BASE_URL = "http://localhost:3000/r/";


const GlobalContext = React.createContext()

export const GlobalProvider = ({children}) => {

    const [incomes, setIncomes] = useState([])
    const [expenses, setExpenses] = useState([])
    const [error, setError] = useState(null)

    
    //calculate incomes
   

    const getIncomes = async () => {
        
        const res = await axios.get(`${BASE_URL}getallinc`)
        setIncomes(res.data)
        console.log(res.data)
    }

    const addIncome = async (income) => {
        console.log("hello") ;
        const res = await axios.post(`${BASE_URL}add-income`, income).then(()=>{
            console.log("post has been made")
        }).catch((err) =>{
            setError(err)
        })
      
       
    }

    const deleteIncome = async (id) => {
        const res  = await axios.delete(`${BASE_URL}delinc/${id}`)
        getIncomes()
    }

    const totalIncome = () => {
        let totalIncome = 0;
        incomes.forEach((income) =>{
            totalIncome = totalIncome + income.amount
        })

        return totalIncome;
    }


    //calculate incomes
    

    const getExpenses = async () => {
        const res = await axios.get(`${BASE_URL}getallexp`)
        console.log("h")
        setExpenses(res.data)
        console.log(res.data)
    }
    const addExpense = async (expens) => {
        console.log("hello")
        const res= await axios.post(`${BASE_URL}add-expense`, expens)
         getExpenses()
         console.log("hello")
    }

    const deleteExpense = async (id) => {
        const res  = await axios.delete(`${BASE_URL}delexp/${id}`)
        getExpenses()
    }

    const totalExpense = () => {
        let totalIncome = 0;
        expenses.forEach((income) =>{
            totalIncome = totalIncome + income.amount
        })

        return totalIncome;
    }


    const totalBalance = () => {
        return totalIncome() - totalExpense()
    }

    const transactionHistory = () => {
        const history = [...incomes, ...expenses]
        history.sort((a, b) => {
            return new Date(b.createdAt) - new Date(a.createdAt)
        })

        return history.slice(0, 3)
    }


    return (
        <GlobalContext.Provider value={{
            addIncome,
            getIncomes,
            incomes,
            deleteIncome,
            expenses,
            totalIncome,
            addExpense,
            getExpenses,
            deleteExpense,
            totalExpense,
            totalBalance,
            transactionHistory,
            error,
            setError
        }}>
            {children}
        </GlobalContext.Provider>
    )
}

export const useGlobalContext = () =>{
    return useContext(GlobalContext)
}
