import React from "react";
import './i_itemstyle.css'

import { useState } from "react";
import Expand from "./expand";
import { useGlobalContext } from "./context/globalcontext";



export default function Item  ({
    id , 
    title , 
    amount , 
    date , 
    Category , 
    note , 
    deleteItem , 
    type}) {
        
   const [show , setshow] = useState(false) ;
 const [incomes , deleteIncome] = useGlobalContext()
    return(
        <div className="item">
<div className="icon">
   
</div>
<div className="content">
    <h5>{title}</h5>
    <p>{amount}</p>

   
                    {incomes.map((income) => {
                            const {_id, title, amount, date, Category, note, type} = income;
                            return <Expand
                                key={_id}
                                id={_id} 
                                title={title} 
                                note ={note}
                                amount={amount} 
                                date={date} 
                                type={type}
                                Category={Category} 
                                deleteItem={deleteIncome}
                            />
                           
                        })}

<button className="expand" onClick={()=>{setshow(!show)}}>{show && <Expand />}</button>
<button className="del-but" onClick={()=>deleteItem(id)}><i className="del"></i></button>   
        </div>
     
    </div>

    )
}
