import React, { useState } from "react";
import './header.css'
function Header(){


  const [active , setactive] = useState(1) ;
    return(
      <header>
        <div>
        <button className="hover-underline-animation" onClick={setactive(1)}>
        Dashboard 
        <i className="fa-duotone fa-chart-column fa-bounce"></i>
        </button>




        <button className="hover-underline-animation" onClick={setactive(2)}>
        Income
        <i className="fa fa-inr"></i>
        </button>

       <button className="hover-underline-animation" onClick={setactive(3)}>
        Expenses
        <i className="fa-regular fa-money-bills"></i>
        </button>
        </div>

        
       <img src={"hello"}></img>


      </header>
    );
}
export default Header;
