import react from 'react'
import IForm from '../form/form';
import Item from '../incomeitem';
import { useGlobalContext } from '../context/globalcontext';
import { useEffect } from 'react';



function Expenses(){
    const [expenses , totalExpense , getExpenses  , deleteExpense] = useGlobalContext()


    useEffect(() =>{
        getExpenses()
    }, [])
    return(
            <>
                    <h1>Expense</h1>
                    <h2 className="total-expense">Total Expense: <span>${totalExpense()}</span></h2>
                   
                        <div className="list-box">
                        <div className="Form">
                        <IForm />
                        </div>
                        {expenses.map((expense) => {
                                const {_id, title, amount, date, Category, note, type} = expense;
                                return <Item
                                    key={_id}
                                    id={_id} 
                                    title={title} 
                                    note ={note}
                                    amount={amount} 
                                    date={date} 
                                    type={type}
                                    Category={Category} 
                                
                                />
                            })}
                        </div>
                   
                    </>
    )
}

export default Expenses
