import react, { useEffect } from 'react'
import './income.css'
import { useGlobalContext } from '../globalcontext'
import '../form'

import { GlobalProvider  } from '../globalcontext'

import IncomeItem from '../incomeitem'
import IForm


from '../form'
function Income(){
  const {addIncome , getIncomes , incomes} = useGlobalContext()



  useEffect(() =>{
     getIncomes()
 }, [])


    return (
        <>
         <h1>Income</h1>
         <div className='total-income'>
             <IForm/>
               </div>


          <div className='list-box'>
               <div/>

          <button className='add-income'>

          </button>


              <div className='income'>
              {incomes.map((income) => {
                            const {_id, title, amount, date, category, note, type} = income;
                            return <IncomeItem
                                key={_id}
                                id={_id} 
                                title={title} 
                                note={note} 
                                amount={amount} 
                                date={date} 
                                type={type}
                                category={category} 
                                indicatorColor="var(--color-green)"
                             
                            />
                        })}

              </div>
         </div>
         </>
    )
}

export default Income
