import React from 'react';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Doughnut } from 'react-chartjs-2';
import { arr } from '../transactions';
import { useEffect } from 'react';
import { useGlobalContext } from '../context/globalcontext';
import { useState } from 'react';
ChartJS.register(ArcElement, Tooltip, Legend);





export  const Chart2 = () => {

    const {incomes , getIncomes } = useGlobalContext()
    const [state , setstate] = useState([])

useEffect(() =>{
    getIncomes()
}, [])



var date = new Date();
      var date2 = new Date(date.getFullYear(), date.getMonth(), 1);
      var date1= new Date() ;
      
   
  
    
   incomes.forEach(element => {
    var x = new Date(element.date)
    if(x.valueOf()<=date1.valueOf() && x.valueOf() >=date2.valueOf() ){
     setstate(element)
    }
    else{
      return 
    }
   })
   console.log("hello") ;
    const data = {
        labels:  state.map((inc)=>{
            const {date} = inc ;
            return date ;
        })  , 
        datasets: [
          {
            label: state.map((inc)=>{
                const {title} = inc ;
                return title ; }) ,
                
                
            data:  state.map((inc)=>{
                const {amount} = inc ;
                return amount ;}),


            backgroundColor: [
              'rgba(255, 99, 132, 0.2)',
             
            ],
            borderColor: [
              'rgba(255, 99, 132, 1)',

            ],
            borderWidth: 1,
          },
        ],
      };
      

        return (
            <div>
                <Doughnut data={data}/>
            </div>
        )
    }
