import Header from "./header";
import Footer from "./footer";

class Layout extends React.Component{

    render(){
      const mystyle = {
        backgroundColor : "brown" ,
        padding : "10px" ,
        border : "20px" ,
        borderColor: "coral" , 
        borderRadius : "10px" ,

      } ;
        return(
        <>
        <Header/>
        <main style={mystyle}>
            
        </main>
            
         <Footer/>
        
        </>
   ) }
}

export default Layout
