import React, { useState } from "react";
import ReactDatePicker from "react-datepicker";
import { useGlobalContext } from "./context/globalcontext";






function Form(){
const {addIncome , getIncomes} = useGlobalContext() ;
   
    const [state , setstate] = useState({
        title  :'' ,
        date :  '' ,
        amount : '' , 
        category : '' ,
        note : '' ,
    })
    

  const { title, amount, date, category,description } = state;


    const handleInput = name => e => {
        setstate({...state, [name]: e.target.value})
      
    }
    const handelsubmit = e=>{
        e.preventDefault() ;
        addIncome(state) ;
        setstate({
            title: '',
            amount: '',
            date: '',
            category: '',
            description: '',
        })
    }
     
  


    return(
        <div onSubmit={handelsubmit()}>
          
            <input type="text"   onChange={handleInput('title')} ></input>
            <input type="date" onChange={(date)=>{
                setstate({...state , date : date}) 
            }}></input>
            <input type="number" onChange={handleInput('amount')}></input>
            <select name="category" onChange={handleInput('category')}>
            <option value=""  disabled >Select Option</option>
                    <option value="salary">Salary</option>
                    <option value="freelancing">Freelancing</option>
                    <option value="investments">Investiments</option>
                    <option value="stocks">Stocks</option>
                    <option value="bitcoin">Bitcoin</option>
                    <option value="bank">Bank Transfer</option>  
                    <option value="youtube">Youtube</option>  
                    <option value="other">Other</option>  
            </select>
            <button className="submitbut"></button>
        </div>

    )
}

export default Form
