import react from 'react'
import EForm from '../expenseitem';
import Item from '../incomeitem';
import { useGlobalContext } from '../context/globalcontext';
import { useEffect } from 'react';



function Expenses(){
    const {addExpense, expenses, getExpenses, deleteExpense , totalExpense} = useGlobalContext()

    useEffect(() =>{
        getExpenses()
    }, [])
    return (
        <>
                <h1>Expenses</h1>
                <h2 className="total-expense">Total Expenses: <span>${totalExpense()}</span></h2>
               
                    <div className="list-box">
                    <div className="Form">
                    <EForm />
                    </div>
                    {expenses.map((expens) => {
                            const {_id, title, amount, date, Category, note, type} = expens ;
                            console.log(expens)
                            return <Item
                                key={_id}
                                id={_id} 
                                title={title} 
                                note ={note}
                                amount={amount} 
                                date={date} 
                                type={type}
                                Category={Category} 
                                deleteItem={deleteExpense}
                            />
                        })}
                    </div>
               
                </>
    )
}


export default Expenses
