        <div className="item">
<div className="icon">
   
</div>
<div className="content">
    <h5>{title}</h5>
    <p>{amount}</p>


<button className="expand" onClick={()=>{ setshow(!show)}}>{show && <p>{title}</p>}
   
</button>

<button className="del-but" onClick={()=>deleteItem(id)}><i className="del"></i></button>   
        </div>
     
    </div>

    )
}
