import React from "react";
import './header.css'
import Dashboard from "../dashboard'/dashboard";
import { useState } from "react";
import Income from "../income/income";
import Expenses from "../expenses/expenses";


function Header(

){
  const [active , setactive ] = useState(1)



  const displayData = ()=>{
    switch(active){
      case 1 :
        return <Dashboard/>
        case 2:
          return<Income/>
        case 3:
          return <Expenses/>
          default:
            return <Dashboard/>
    }
  }
    return(
      <header>
        <div>
        <button className="hover-underline-animation" onClick={active(1)}>
        Dashboard 
        <i className="fa-duotone fa-chart-column fa-bounce"></i>
        </button>

        <button className="hover-underline-animation">
        Transactions
        <i className="fa-sharp fa-regular fa-money-simple-from-bracket"></i>
        </button>


        <button className="hover-underline-animation" onClick={active(2)}>
        Income
        <i className="fa fa-inr"></i>
        </button>

       <button className="hover-underline-animation" onClick={active(3)}>
        Expenses
        <i className="fa-regular fa-money-bills"></i>
        </button>
        </div>

        
       <img src={"hello"}></img>


      </header>
    );
}
export default Header;
