import React from 'react';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Doughnut } from 'react-chartjs-2';
import Transactions from '../transactions';

ChartJS.register(ArcElement, Tooltip, Legend);

const {arr} = Transactions()

export  const Chart2 = () => {
   
    const data = {
        labels:  arr.map((inc)=>{
            const {date} = inc ;
            return date ;
        })  , 
        datasets: [
          {
            label: arr.map((inc)=>{
                const {title} = inc ;
                return title ; }) , 
            data:  arr.map((inc)=>{
                const {amount} = inc ;
                return amount ;}),
            backgroundColor: [
              'rgba(255, 99, 132, 0.2)',
              'rgba(54, 162, 235, 0.2)',
              'rgba(255, 206, 86, 0.2)',
              'rgba(75, 192, 192, 0.2)',
              'rgba(153, 102, 255, 0.2)',
              'rgba(255, 159, 64, 0.2)',
            ],
            borderColor: [
              'rgba(255, 99, 132, 1)',
              'rgba(54, 162, 235, 1)',
              'rgba(255, 206, 86, 1)',
              'rgba(75, 192, 192, 1)',
              'rgba(153, 102, 255, 1)',
              'rgba(255, 159, 64, 1)',
            ],
            borderWidth: 1,
          },
        ],
      };
      

        return (
            <div>
                <Doughnut data={data}/>
            </div>
        )
    }
