import React from "react";
import ReactDatePicker from "react-datepicker";
import { useGlobalContext } from "./context/globalcontext";
import 'react-datepicker/dist/react-datepicker.css';
import { useState } from "react";
import { Chart } from "chart.js";
import axios from "axios";
import { useEffect } from "react";
import Item from "./incomeitem";

 export default function Transactions(){

    const {incomes , expenses , getIncomes , deleteIncome} = useGlobalContext() ;
    useEffect(() =>{
      getIncomes()
  }, [])
    
      //i can just check for all income and expenses 
      var date = new Date();
      var date2 = new Date(date.getFullYear(), date.getMonth(), 1);
      var date1= new Date() ;
      
   const arr = [] ;


      
      return (<>
    
 {incomes.forEach(element => {
  var x = new Date(element.date)
  if(x.valueOf()<=date1.valueOf() && x.valueOf() >=date2.valueOf()){
    arr.push(x)
  }
  
 })
 }
  <div className='income-list'>
    {arr.map((income) => {
      const {_id, title, amount, date, Category, note} = income;
      console.log(income)
      return <Item
          key={_id}
          id={_id} 
          title={title} 
          note ={note}
          amount={amount} 
          date={date} 
          Category={Category} 
          deleteItem={deleteIncome}
      />
  })}
</div>

{expenses.forEach(element => {
  var x = new Date(element.date)
  console.log(x.valueOf())
  if(x.valueOf()<date1.valueOf() && x.valueOf() >date2.valueOf()){
    console.log(element) ;
  }
 })}
    
  
 
</>
      )
    }
