import React from 'react';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Doughnut } from 'react-chartjs-2';
import { arr } from '../transactions';
import { useEffect } from 'react';
import { useGlobalContext } from '../context/globalcontext';
ChartJS.register(ArcElement, Tooltip, Legend);





export  const Chart2 = () => {

    const {getIncomes , state} = useGlobalContext()


useEffect(() =>{
    getIncomes()
}, [])
   console.log("hello") ;
    const data = {
        labels:  state.map((inc)=>{
            const {date} = inc ;
            return date ;
        })  , 
        datasets: [
          {
            label: state.map((inc)=>{
                const {title} = inc ;
                return title ; }) ,
                
                
            data:  state.map((inc)=>{
                const {amount} = inc ;
                return amount ;}),


            backgroundColor: [
              'rgba(255, 99, 132, 0.2)',
             
            ],
            borderColor: [
              'rgba(255, 99, 132, 1)',

            ],
            borderWidth: 1,
          },
        ],
      };
      

        return (
            <div>
                <Doughnut data={data}/>
            </div>
        )
    }
